def OnTriggered(a):
    global list
    if me.opponent.hand.size() > 0:
        hand = CardList()
        list = CardList()
        for c in me.opponent.hand:
            hand.add(c)
            if game.IsCardLegal(me, c):
                list.add(c)
        for c in hand:
            game.MoveCard(me.opponent, c, me.opponent.hand, me.opponent.hand, 3)
        if list.size() > 0:
          game.PushEventResume(me, card, "OnPicked", list)
          game.PickCard(me, list, True, game.PICK_TYPE_ATTACK)

