def OnAttack(a):
    global list
    heroes = {}
    list = CardList()
    c = me.stock.Top()
    if c:
        list.add(c)
        game.ShowCard(me, c)
        for h in c.getHeroes():
            heroes[h] = True
    c = me.stock.Top()
    if c:
        list.add(c)
        game.ShowCard(me, c)
        for h in c.getHeroes():
            heroes[h] = True
    if heroes:
        a.attackBonus += len(heroes)
        game.PushEventResume(me, card, "OnPicked", list)
        game.PickCard(me, list, False, game.PICK_TYPE_DECK)

def OnPicked(c):
    if c:
        game.MoveCard(me, c, me.show, me.stock, 0)
        list.remove(c)
        if list.size() > 0:
            game.PushEventResume(me, card, "OnPicked2", list)
            game.PickCard(me, list, False, game.PICK_TYPE_DECK)

def OnPicked2(c):
    if c:
        game.MoveCard(me, c, me.show, me.stock, 0)

def OnAttack(a):
  global atk
  global revealed
  atk = a
  revealed = CardList()
  PickCard()

def PickCard():
  choices = CardList()
  for k in me.hand:
      if not revealed.contains(k):
          choices.add(k)
  if choices.size() > 0:
      game.PushEventResume(me, card, 'OnPicked', choices)
      game.PickCard(me, choices, True, game.PICK_TYPE_REVEAL, 1)
  else:
      Done()

def OnPicked(c):
  if c:
    revealed.add(c)
    game.RevealCard(me,c)
    PickCard()
  else:
    Done()

def Done():
    heroes = {}
    for k in revealed:
        for f in k.getHeroes():
            heroes[f] = True
    atk.attackBonus += len(heroes)

trigger = 2
def OnTriggered(a):
    global list
    heroes = {}
    list = CardList()
    c = me.stock.Top()
    if c:
        list.add(c)
        game.ShowCard(me, c)
        for h in c.getHeroes():
            heroes[h] = True
    c = me.stock.Top()
    if c:
        list.add(c)
        game.ShowCard(me, c)
        for h in c.getHeroes():
            heroes[h] = True
    if len(heroes) > 1:
        for c in list:
            game.MoveCard(me, c, me.show, me.hand, 1)
    else:
        game.PushEventResume(me, card, "OnPicked", list)
        game.PickCard(me, list, False, game.PICK_TYPE_DECK)

