def OnAttack(a):
    global cards
    cards = CardList()
    i = 0
    c = me.stock.Top()
    while c and i < 5:
        cards.add(c);
        game.ShowCard(me, c)
        c = me.stock.Top()
        i = i + 1

    if cards.size() > 0:
        OnPicked(cards[0])

def OnPicked(c):
    if c:
        game.PushEventResume(me, card, "OnPicked", cards)
        game.PickCard(me, cards, True, game.PICK_TYPE_DONE, 1)
    else:
        chars = {}
        c = me.show.Top()
        while c:
            game.MoveCard(me, c, me.show, me.stock, 0)
            for h in c.getHeroes():
                chars[h] = True
            c = me.show.Top()
        a.attackBonus += len(chars)
        game.Shuffle(me.stock)

