def OnEventResume(compare):
    if compare:
        game.MoveCard(me, compare, me.hand, me.discard, 3)
        list = []
        valid = CardList()
        for c in me.opponent.hand:
            list.append(c)
        for c in list:
            add = False
            game.MoveCard(me.opponent, c, me.opponent.hand, me.opponent.hand, 3)
            for f in compare.getAttacks():
              if c.hasFactor(f):
                add = True
            if c.getLevel() == compare.getLevel():
              add = True
            if c.getBlock() == compare.getBlock():
              add = True
            if add:
              valid.add(c)
        for c in valid:
            game.MoveCard(me.opponent, c, me.opponent.hand, me.opponent.discard, 3)

