def PickCard():
  choices = CardList()
  for k in me.hand:
      if not revealed.contains(k):
          choices.add(k)
  if choices.size() > 0:
      game.PushEventResume(me, card, 'OnPicked', choices)
      game.PickCard(me, choices, True, game.PICK_TYPE_REVEAL, 1)
  else:
      Done()

def OnPicked(c):
  if c:
    revealed.add(c)
    game.RevealCard(me,c)
    PickCard()
  else:
    Done()

def Done():
    factors = {}
    for k in revealed:
        for f in k.getAttacks():
            factors[f] = True
    atk.attackBonus += len(factors)

