def OnBlocked(c):
    if c.getOwner() == me.opponent:
      game.PushDamage(me, card, 2, False, False)

def OnAttack(a):
    hand = CardList()
    chars = {}
    for c in me.opponent.hand:
        hand.add(c)
    for c in hand:
        game.MoveCard(me.opponent, c, me.opponent.hand, me.opponent.hand, 3)
        for h in c.getHeroes():
            chars[h] = True
    a.attackBonus += len(chars)

