def OnOpponentStockDepleted(c):
    oldHand = CardList()
    for hc in me.opponent.hand:
       oldHand.add(hc)
    for hc in oldHand:
       game.MoveCard(me.opponent, hc, me.opponent.hand, me.opponent.discard, Card.FACE_UP)

def OnDeployKeeper(c):
    if c == card:
      if me.opponent.stock.size() == 0:
        OnOpponentStockDepleted(card)

